import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getTodos, addTodo, updateTodo, deleteTodo } from './actions/todoActions';
import Modal from './Modal'
import SabpaisaPaymentGateway from './SabpaisaPaymentGateway';

const TodoApp = () => {
    const [newTodo, setNewTodo] = useState('');
    const [editTodo, setEditTodo] = useState(null);
    const dispatch = useDispatch();
    const { todos, loading, error } = useSelector(state => state.todos);

    useEffect(() => {
      dispatch(getTodos());
    }, [dispatch]);

    const handleAddTodo = () => {
      if (newTodo.trim()) {
        dispatch(addTodo(newTodo));
        setNewTodo('');
      }
    };

    const handleUpdateTodo = (updatedTodo) => {
      dispatch(updateTodo(updatedTodo._id, updatedTodo));
    };

    const handleDeleteTodo = (id) => {
      dispatch(deleteTodo(id));
    };

    const openEditPopup = (todo) => {
      setEditTodo(todo);
    };

    const closeEditPopup = () => {
      setEditTodo(null);
    };
    const removePay=()=>{
      localStorage.setItem('paymentStatus'," ")
      localStorage.setItem('paymentSuccess'," ")
    }
  return (
    <div>
        <SabpaisaPaymentGateway/>
        <h1>Todo List</h1>
      <input
        type="text"
        value={newTodo}
        onChange={(e) => setNewTodo(e.target.value)}
      />
      <button onClick={handleAddTodo}>Add Todo</button>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {!loading && !error && (
        <ul>
          {todos.map(todo => (
            <li key={todo._id}>
              <span
                onClick={() => handleUpdateTodo({ ...todo, completed: !todo.completed })}
                style={{ textDecoration: todo.completed ? 'line-through' : 'none', cursor: 'pointer' }}
              >
                {todo.title}
              </span>
              <button onClick={() => openEditPopup(todo)}>Edit</button>
              <button onClick={() => handleDeleteTodo(todo._id)}>Delete</button>
              <button onClick={()=>removePay}>Unsubscribe</button>
            </li>
          ))}
        </ul>
      )}

      {editTodo && (
        <Modal
          todo={editTodo}
          onSave={handleUpdateTodo}
          onClose={closeEditPopup}
        />
      )}
    </div>
  )
}

export default TodoApp
