    if (status === 'SUCCESS') {
      setsuccess(true)
      console.log(success)
    } else {
      setsuccess(false)
    }
  }, [window.location.href]);
  if (!success) 
    return <Navigate to="/payment"/>
  else
  return children;
}

export default ProtectedRoute;
