// SabpaisaPaymentGateway.js
import React, { useEffect } from 'react';
import { PaymentInitModal } from 'pg-test-project';
import { useNavigate } from 'react-router-dom';
function SabpaisaPaymentGateway({ setIsPaymentSuccessful }) {
  const navigation = useNavigate();

  // Placeholder for checking payment success, replace with actual payment logic
  useEffect(() => {
    // Assuming some method to detect payment success; adjust to your setup
    const paymentCompleted = true; // Replace this with actual payment check

    if (paymentCompleted) {
      setIsPaymentSuccessful(true); // Update the state to indicate successful payment
      navigate("/protected")  // Redirect to the protected route
    }
  }, [setIsPaymentSuccessful, navigation]);

  return (
    <div>
      <PaymentInitModal
        // Pass necessary props to your PaymentInitModal
        env={'stag'}
        // Add any additional props required
      />
    </div>
  );
}

export default SabpaisaPaymentGateway;
